import React, { useState , useEffect, useCallback} from 'react';
import './Slideshow.css'; // Optional: Create a separate CSS file for styles

// Slide Component
const Slide = ({ character, line, bgColor = 'bg-black', textColor = 'text-white' }) => (
  <div className={`flex items-center justify-center w-full h-screen ${bgColor} ${textColor} p-8`}>
    <div className="text-center max-w-4xl">
      <h1 className="text-4xl font-bold mb-4">{character}</h1>
      <p className="text-2xl italic">"{line}"</p>
    </div>
  </div>
);

// Slideshow Component
const Slideshow2 = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const slides = [
    { "character": "FANG DASHENG", "line": "Oh it’s you! What did you want me for?" },
    { "character": "CHEN BAILU", "line": "What are you up to? Why not come out here and mix with the others?" },
    { "character": "FANG DASHENG", "line": "I’ve been having a chat with the Shrimp, your adopted daughter. She’s a sweet child." },
    { "character": "CHEN BAILU", "line": "And now how about coming out here for a chat with us. Come and join us, don’t be so unkind." },
    { "character": "FANG DASHENG", "line": "No, no, I’d rather talk with your adopted daughter." },
    { "character": "CHEN BAILU", "line": "There’s just no way getting him to do anything." },
    { "character": "PAN YUETING", "line": "Mr. Jin’s bought three million of the same bonds as you." },
    { "character": "LI SHIQING", "line": "So long as the information’s reliable that Mr. Jin is buying, then of course he’s expecting a rise." },
    { "character": "PAN YUETING", "line": "It must not be unreliable, it must be right." },
    { "character": "GEORGY ZHANG", "line": "It’s hard to live in China, nowhere to live in comfort." },
    { "character": "FANG DASHENG", "line": "The Shrimp! Zhujun, have you seen the Shrimp?" },
    { "character": "CHEN BAILU", "line": "Why isn’t she in the room?" },
    { "character": "FANG DASHENG", "line": "No, she’s gone. I saw her go down in the lift with two or three men." },
    { "character": "CHEN BAILU", "line": "Yueting, see what happens when I ask you to do something for me!" },
    { "character": "PAN YUETING", "line": "Bailu!" },
    { "character": "GEORGY ZHANG", "line": "Look! Just another mad one!" },
    { "character": "WANG FUSHENG", "line": "Bah! Nothing but 'Hai-ei-hai'! By the time the building’s finished we’ll all be driven to madness." },
    { "character": "WANG FUSHENG", "line": "Listen to this! Still at it! They keep singing and ramming all day and night!" },
    { "character": "FANG DASHENG", "line": "Hush! Listen to their singing. It’s cheerful, despite their labor." },
    { "character": "WANG FUSHENG", "line": "Born to slave in poverty. Do you think they enjoy this work?" },
    { "character": "FANG DASHENG", "line": "Who’s been building this? Is it for the rich?" },
    { "character": "WANG FUSHENG", "line": "It’s being built by Da Feng Bank, Mr. Pan, and probably Mrs. Gu too." },
    { "character": "HUANG XINGSAN", "line": "But sir, I’m a clerk at Da Feng Bank. I’m Huang." },
    { "character": "WANG FUSHENG", "line": "Do you know me or not?" },
    { "character": "HUANG XINGSAN", "line": "I dare not say I do." },
    { "character": "WANG FUSHENG", "line": "Then march out! Get out, and don’t return unless you want trouble!" },
  ];

const nextSlide = useCallback(() => {
    setCurrentSlide((prevSlide) => (prevSlide + 1) % slides.length);
  }, [slides.length]);

  // Function to go to the previous slide
const prevSlide = useCallback(() => {
    setCurrentSlide((prevSlide) => (prevSlide - 1 + slides.length) % slides.length);
  }, [slides.length]);

  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.key === 'ArrowRight') {
        nextSlide(); // Right arrow key pressed
      } else if (event.key === 'ArrowLeft') {
        prevSlide(); // Left arrow key pressed
      }
    };

    window.addEventListener('keydown', handleKeyDown); // Add event listener for keydown

    return () => {
      window.removeEventListener('keydown', handleKeyDown); // Clean up event listener on unmount
    };
  }, [nextSlide, prevSlide]);

  return (
    <div className="relative">
      <Slide {...slides[currentSlide]} />
      <button
        className="absolute top-3/4 left-4 bg-white text-black p-2 rounded"
        onClick={prevSlide}
      >
        Previous
      </button>

      <button
        className="absolute top-3/4 right-4 bg-white text-black p-2 rounded"
        onClick={nextSlide}
      >
        Next
      </button>
      <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 text-white">
        {currentSlide + 1} / {slides.length}
      </div>
    </div>
  );
};

export default Slideshow2;
